window.onscroll=function(){fixedNavbar()};let navbar=document.getElementById("nav"),offset=navbar.offsetTop,indent=document.getElementById("section_2");function fixedNavbar(){window.pageYOffset>=offset?(navbar.classList.add("fixed"),indent.style.marginTop=navbar.offsetHeight+"px"):(navbar.classList.remove("fixed"),indent.style.marginTop=0)}const menu_Links=document.querySelectorAll(".menu_link[data-goto]");if(menu_Links.length>0){function onMenuLinkClick(e){const t=e.target;if(t.dataset.goto&&document.querySelector(t.dataset.goto)){const n=document.querySelector(t.dataset.goto).getBoundingClientRect().top+pageYOffset-document.getElementById("nav").offsetHeight;window.scrollTo({top:n,behavior:"smooth"}),e.preventDefault()}}menu_Links.forEach(e=>{e.addEventListener("click",onMenuLinkClick)})}